{
    timestamps: false
})

const Gallery = db.sequelize.define('gallery', {
    id: {type: DataTypes.INTEGER, primaryKey: true, autoIncrement: true}
},
{
    timestamps: false
})

const GalleryItem = db.sequelize.define('gallery_item', {
